import React, { useState } from 'react';
import Header from '../../components/Header/Header';
import './Product.css';

const Product = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([
    { id: 1, vendor: 'Vendor A', name: 'Product 1', category: 'Category 1', link: '#' },
    { id: 2, vendor: 'Vendor B', name: 'Product 2', category: 'Category 2', link: '#' },
  ]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    id: products.length + 1,
    vendor: '',
    name: '',
    category: '',
    description: '',
    link: '',
    images: []
  });

  const filteredProducts = products.filter(product =>
    product.id.toString().includes(searchTerm) ||
    product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    product.vendor.toLowerCase().includes(searchTerm.toLowerCase()) ||
    product.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const clearSearch = () => {
    setSearchTerm('');
  };

  const handleEdit = (product) => {
    setCurrentProduct(product);
    setIsModalOpen(true);
  };

  const handleDelete = (productId) => {
    setProducts(products.filter(product => product.id !== productId));
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setNewProduct({
      id: products.length + 1,
      vendor: '',
      name: '',
      category: '',
      description: '',
      link: '',
      images: []
    });
    setCurrentProduct(null);
  };

  const handleProductChange = (e) => {
    const { name, value } = e.target;
    if (currentProduct) {
      setCurrentProduct(prevState => ({ ...prevState, [name]: value }));
    } else {
      setNewProduct(prevState => ({ ...prevState, [name]: value }));
    }
  };

  const handleImagesChange = (e) => {
    const files = [...e.target.files];
    if (currentProduct) {
      setCurrentProduct(prevState => ({ ...prevState, images: files }));
    } else {
      setNewProduct(prevState => ({ ...prevState, images: files }));
    }
  };

  const handleSaveProduct = () => {
    if (currentProduct) {
      setProducts(products.map(product => product.id === currentProduct.id ? currentProduct : product));
    } else {
      setProducts([...products, newProduct]);
      setNewProduct({
        id: products.length + 1,
        vendor: '',
        name: '',
        category: '',
        description: '',
        link: '',
        images: []
      });
    }
    closeModal();
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className='col-lg-1'></div>
        <div className="col-lg-11">
          <Header />
          <div className='mt-5'>
            <div className="row mb-3">
              <div className="col-lg-6">
                <button className="add-product-btn" onClick={() => setIsModalOpen(true)}>
                  <i className="fas fa-plus"></i> ADD PRODUCT
                </button>
              </div>
              <div className="col-lg-6 search-bar-container">
                <input 
                  type="text" 
                  placeholder="Search by ID, Name, Vendor" 
                  className="search-bar" 
                  value={searchTerm} 
                  onChange={(e) => setSearchTerm(e.target.value)} 
                />
                {searchTerm && (
                  <button className="clear-search-btn" onClick={clearSearch}>×</button>
                )}
              </div>
            </div>
            <div className="row">
              <div className="table-container">
                <table className="styled-table">
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>VENDOR</th>
                      <th>PRODUCT NAME</th>
                      <th>PRODUCT CATEGORY</th>
                      <th>LINK</th>
                      <th>ACTION</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredProducts.map(product => (
                      <tr key={product.id}>
                        <td>{product.id}</td>
                        <td>{product.vendor}</td>
                        <td>{product.name}</td>
                        <td>{product.category}</td>
                        <td><a href={product.link}>Link</a></td>
                        <td className="action-icons">
                          <i className="fas fa-edit editicon" onClick={() => handleEdit(product)}></i>
                          <i className="fas fa-trash trashicon" onClick={() => handleDelete(product.id)}></i>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <span className="close" onClick={closeModal}>&times;</span>
              <h2>{currentProduct ? 'Edit Product' : 'Add Product'}</h2>
            </div>
            <form>
              <div className="form-group my-5">
                <input 
                  type="text" 
                  className="form-control" 
                  value={currentProduct ? currentProduct.id : newProduct.id} 
                  readOnly 
                />
                <label className="form-label">Product ID</label>
              </div>
              <div className="form-group my-5">
                <input 
                  type="text" 
                  className="form-control" 
                  name="vendor" 
                  value={currentProduct ? currentProduct.vendor : newProduct.vendor} 
                  onChange={handleProductChange} 
                />
                <label className="form-label">Vendor</label>
              </div>
              <div className="form-group my-5">
                <input 
                  type="text" 
                  className="form-control" 
                  name="name" 
                  value={currentProduct ? currentProduct.name : newProduct.name} 
                  onChange={handleProductChange} 
                />
                <label className="form-label">Product Name</label>
              </div>
              <div className="form-group my-5">
                <select 
                  className="form-control" 
                  name="category" 
                  value={currentProduct ? currentProduct.category : newProduct.category} 
                  onChange={handleProductChange}
                >
                  <option value="">Select Category</option>
                  <option value="Category 1">Category 1</option>
                  <option value="Category 2">Category 2</option>
                </select>
                <label className="form-label">Product Category</label>
              </div>
              <div className="form-group my-5">
                <textarea 
                  className="form-control" 
                  name="description" 
                  value={currentProduct ? currentProduct.description : newProduct.description} 
                  onChange={handleProductChange} 
                />
                <label className="form-label">Description</label>
              </div>
              <div className="form-group my-5">
                <input 
                  type="text" 
                  className="form-control" 
                  name="link" 
                  value={currentProduct ? currentProduct.link : newProduct.link} 
                  onChange={handleProductChange} 
                />
                <label className="form-label">Product Link</label>
              </div>
              <div className="form-group my-5">
                <input 
                  type="file" 
                  multiple 
                  className="form-control" 
                  onChange={handleImagesChange} 
                />
                <label className="form-label">Product Images</label>
              </div>
            </form>
            <div className="modal-footer">
              <button className="btn btn-green" onClick={handleSaveProduct}>{currentProduct ? 'Save Changes' : 'Add Product'}</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Product;
